// Extraire les informations de l'article depuis le XML
const xml = $input.item.json.data;

// Fonction pour extraire du texte depuis le XML
function extractText(xml, startTag, endTag) {
  const start = xml.indexOf(startTag);
  if (start === -1) return '';
  const end = xml.indexOf(endTag, start);
  if (end === -1) return '';
  return xml.substring(start + startTag.length, end).trim();
}

// Fonction pour extraire tous les √©l√©ments d'un tag
function extractAll(xml, tag) {
  const regex = new RegExp(`<${tag}[^>]*>([^<]+)</${tag}>`, 'g');
  const matches = [];
  let match;
  while ((match = regex.exec(xml)) !== null) {
    matches.push(match[1]);
  }
  return matches;
}

// Extraction du PMID
const pmid = extractText(xml, '<PMID Version="1">', '</PMID>');

// Extraction du titre
const title = extractText(xml, '<ArticleTitle>', '</ArticleTitle>');

// Extraction de l'abstract
let abstract = '';
const abstractTexts = extractAll(xml, 'AbstractText');
if (abstractTexts.length > 0) {
  abstract = abstractTexts.join('\n\n');
}

// Extraction des auteurs
const lastNames = extractAll(xml, 'LastName');
const forenames = extractAll(xml, 'ForeName');
const authors = [];
for (let i = 0; i < Math.min(lastNames.length, forenames.length); i++) {
  authors.push(`${forenames[i]} ${lastNames[i]}`);
}
const authorsString = authors.length > 0 ? authors.join(', ') : 'Auteurs non disponibles';

// Extraction du journal
const journal = extractText(xml, '<Title>', '</Title>');

// Extraction de l'ann√©e de publication
const year = extractText(xml, '<Year>', '</Year>');

// Extraction du DOI
const doi = extractText(xml, '<ELocationID EIdType="doi" ValidYN="Y">', '</ELocationID>');

